sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // implement resource here
    var req = request.body.data;
    gs.info("[telegram bot request] " + JSON.stringify(req));
	var msg = '';

    if (req.message.text == "/start") {
        msg = {
            text: 'Hi! If this if your first time here, we need to check your phone number!',
            chat_id: req.message.chat.id.toString(),
            reply_markup: {
                keyboard: [
                    [
                        {
                            text: "Send phone number",
                            request_contact: true
                        }
                    ], [

                        {
                            text: "No thanks."
                        }
                    ]
                ],
                resize_keyboard: true,
                one_time_keyboard: true
            }
        };
        sendMessage(msg);
    }

    if (req.message.contact.phone_number) {
        msg = {
            text: 'Thanks!',
            chat_id: req.message.chat.id.toString()
        };
        sendMessage(msg);
    }

    function sendMessage(body) {
        try {
            var r = new sn_ws.RESTMessageV2('x_550467_telegram.TelegramBot', 'sendMessage');
            r.setRequestBody(JSON.stringify(body));

            var res = r.execute();
            var responseBody = res.getBody();
            var httpStatus = res.getStatusCode();
            gs.info("[telegram bot response " + httpStatus.toString() + "] " + responseBody.toString());
        }
        catch (ex) {
            var message = ex.message;
            gs.info("[telegram bot error] " + message.toString());
        }
    }

})(request, response);
